import './App.css';
import { animals } from './components/animals';

let images = [];
const showBackground = true;

function displayFact(e) {
  let facts = animals[e.target.alt].facts;
  document.getElementById('fact').innerHTML = facts[Math.floor(Math.random() * facts.length)];
}

for (const animal in animals) {
  images.push( 
    <img
      key={animal}
      className='animal'
      alt={animal}
      src={animals[animal].image}
      aria-label={animal}
      role='button'
      onClick={displayFact}
    />
  );
}

const background = <img alt="ocean" src="https://i0.wp.com/treasureearthprize.org/wp-content/uploads/2018/12/pretty-ocean-wallpaper-475-541-hd-wallpapers.jpg?ssl=1" />;
const animalFacts = (
  <div>
    <h1 className="title">Click an animal for a fun fact</h1>
    {showBackground && background}
    <p id='fact'></p>
    <div className="animals">
      {images}
    </div>
  </div>
);

function App() {
  return (
    <div className="App">
      {animalFacts}
    </div>
  );
}

export default App;
